const executionId = this.getExecutionId ? this.getExecutionId() : Date.now().toString();

function respond(statusCode, body) {
  return [{ json: { httpStatus: statusCode, body } }];
}

function errorEnvelope(requestId, code, message, details) {
  return {
    request_id: requestId || null,
    status: 'error',
    correlation_id: executionId,
    data: null,
    error: { code, message, details: details || {} }
  };
}

function okEnvelope(requestId, data) {
  return {
    request_id: requestId,
    status: 'ok',
    correlation_id: executionId,
    data,
    error: null
  };
}

const notionToken = $env.NOTION_API_KEY;
if (!notionToken) {
  return respond(500, errorEnvelope(null, 'config_error', 'NOTION_API_KEY is not set', {}));
}

const baseUrl = 'https://api.notion.com/v1';
const notionHeaders = {
  Authorization: `Bearer ${notionToken}`,
  'Notion-Version': '2022-06-28',
  'Content-Type': 'application/json'
};

const request = async (method, urlPath, bodyData) => {
  const options = {
    method,
    url: `${baseUrl}${urlPath}`,
    headers: notionHeaders,
    json: true
  };
  if (bodyData) options.body = bodyData;
  return this.helpers.httpRequest(options);
};

const titleText = (text) => ({ title: [{ text: { content: text } }] });
const richText = (text) => ({ rich_text: [{ text: { content: text } }] });

const getTitleFromPage = (page) => {
  if (!page || !page.properties) return '';
  const titleKey = Object.keys(page.properties).find((key) => page.properties[key].type === 'title');
  if (!titleKey) return '';
  const titleArray = page.properties[titleKey].title || [];
  return titleArray.map((t) => t.plain_text).join('');
};

const getTitleFromDatabase = (db) => {
  if (!db || !Array.isArray(db.title)) return '';
  return db.title.map((t) => t.plain_text).join('');
};

const queryDatabase = async (dbId, filter) => {
  return request('POST', `/databases/${dbId}/query`, { filter, page_size: 1 });
};

const getPropertyText = (page, propName) => {
  const prop = page.properties[propName];
  if (!prop) return '';
  if (prop.type === 'rich_text') return (prop.rich_text || []).map((t) => t.plain_text).join('');
  if (prop.type === 'title') return (prop.title || []).map((t) => t.plain_text).join('');
  return '';
};

const findDatabaseByTitle = async (title) => {
  const res = await request('POST', '/search', {
    query: title,
    page_size: 10,
    filter: { property: 'object', value: 'database' }
  });
  return (res.results || []).find((db) => getTitleFromDatabase(db) === title) || null;
};

const findRegistryDbId = async () => {
  const registryDb = await findDatabaseByTitle('OS Registry');
  return registryDb ? registryDb.id : null;
};

const findRegistryValue = async (registryDbId, key) => {
  const res = await queryDatabase(registryDbId, {
    property: 'Key',
    title: { equals: key }
  });
  const page = (res.results || [])[0];
  if (!page) return null;
  return getPropertyText(page, 'Value');
};

const resolveLedgerDbId = async (registryDbId) => {
  if (!registryDbId) return null;
  const ledgerId = await findRegistryValue(registryDbId, 'ledger_db_id');
  if (ledgerId) return ledgerId;
  const ledgerDb = await findDatabaseByTitle('Request Ledger');
  return ledgerDb ? ledgerDb.id : null;
};

const resolveDatabaseId = async (registryDbId, databaseKey) => {
  if (!registryDbId) return null;
  if (databaseKey === 'registry') return registryDbId;
  if (databaseKey === 'request_ledger') return await resolveLedgerDbId(registryDbId);
  const key = `${databaseKey}_db_id`;
  const dbId = await findRegistryValue(registryDbId, key);
  return dbId || null;
};

const findLedgerEntry = async (ledgerDbId, idempotencyKey) => {
  const res = await queryDatabase(ledgerDbId, {
    property: 'Idempotency Key',
    title: { equals: idempotencyKey }
  });
  return (res.results || [])[0] || null;
};

const createLedgerEntry = async (ledgerDbId, data) => {
  const properties = {
    'Idempotency Key': titleText(data.idempotencyKey),
    'Request ID': richText(data.requestId),
    'Endpoint': richText(data.endpoint),
    'Status': { select: { name: data.status } },
    'Result Object ID': richText(data.resultObjectId || ''),
    'Timestamp': { date: { start: data.timestamp } },
    'Raw Request': richText(data.rawRequest || '')
  };
  return request('POST', '/pages', {
    parent: { database_id: ledgerDbId },
    properties
  });
};

const getPage = async (pageId) => request('GET', `/pages/${pageId}`);


const headers = $json.headers || {};
const authHeader = (headers.authorization || headers.Authorization || '').trim();
const bearerToken = $env.API_BEARER_TOKEN || '';

if (!bearerToken) {
  return respond(500, errorEnvelope(null, 'config_error', 'No bearer token configured', {}));
}

const expectedAuth = `Bearer ${bearerToken}`;

if (authHeader !== expectedAuth) {
  return respond(401, errorEnvelope(null, 'unauthorized', 'Invalid or missing Authorization header', {}));
}

const body = $json.body;
if (!body || typeof body !== 'object') {
  return respond(400, errorEnvelope(null, 'invalid_request', 'Request body must be a JSON object', {}));
}

const { request_id, actor, payload } = body;
if (typeof request_id !== 'string' || typeof actor !== 'string' || typeof payload !== 'object' || payload === null) {
  return respond(400, errorEnvelope(request_id, 'invalid_request', 'Missing or invalid request envelope fields', {}));
}

const { database_key } = payload;
if (typeof database_key !== 'string' || !database_key.trim()) {
  return respond(400, errorEnvelope(request_id, 'invalid_request', 'payload.database_key is required', {}));
}

try {
  const registryDbId = await findRegistryDbId();
  if (!registryDbId) {
    return respond(500, errorEnvelope(request_id, 'registry_not_found', 'OS Registry database not found', {}));
  }

  const databaseId = await resolveDatabaseId(registryDbId, database_key);
  if (!databaseId) {
    return respond(400, errorEnvelope(request_id, 'invalid_request', 'Unknown database_key', { database_key }));
  }

  const db = await request('GET', `/databases/${databaseId}`);

  return respond(200, okEnvelope(request_id, {
    database_key,
    database_id: databaseId,
    database_name: getTitleFromDatabase(db),
    properties: db.properties,
    last_edited_time: db.last_edited_time
  }));
} catch (error) {
  const message = error && error.message ? error.message : 'Schema fetch failed';
  return respond(500, errorEnvelope(request_id, 'schema_failed', message, { stack: error && error.stack }));
}
